class Solution {
public:
const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(),i,j,prev,ans=0;
    vector<vector<int>> dp(n, vector<int>(51, 0));

    for ( i = 0; i <= nums[0]; i++) {
        dp[0][i] = 1;
    }

    for ( i = 1; i < n; i++) {
        for ( j = 0; j <= nums[i]; j++) {
            for ( prev = 0; prev <= j; prev++) {
                if (nums[i] - j <= nums[i - 1] - prev) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][prev]) % MOD;
                }
            }
        }
    }

    for ( i = 0; i <= nums[n - 1]; i++) {
        ans += (dp[n - 1][i]) % MOD;
        ans%=MOD;
    }
    return ans;
    }
};
