class Solution {
public:
    const int mod=1e9+7;
    int solve(int i,int a,int b,vector<int>& nums,vector<vector<vector<int>>>& dp)
    {
        int n=nums.size();
        if(i==n) return 1;
        if(dp[i][a][b]!=-1) return dp[i][a][b];
        int ans=0;
        for(int j=a;j<=nums[i];j++)
        {
            int k=nums[i]-j;
            if(k<=b) ans=(ans+solve(i+1,j,k,nums,dp))%mod;
        }
        return dp[i][a][b]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(0,0,nums[0],nums,dp);
    }
};
