class Solution {
public:
    int find(int n,vector<int>&nums,int a1,int a2,vector<vector<vector<int>>>&dp){
        if(n==nums.size())return 1;

        if(dp[n][a1][a2]!=-1)return dp[n][a1][a2];

        int count=0;
        for(int i=a1;i<=nums[n];i++){
            int y=nums[n]-i;
            if(y<=a2){
                count=(count+find(n+1,nums,i,y,dp))%1000000007;
            }

        }

        return dp[n][a1][a2]=count%1000000007;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size()+1,vector<vector<int>>(52,vector<int>(52,-1)));
        return find(0,nums,0,51,dp);

        // vector<vector<int>> dp(nums.size(),vector<int>(2,-1));


    }
};
