class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        curr = [0]
        dp = {}

        def dfs(min_1, max_2, index):
            if index >= n:
                curr[0] += 1
                return

            if (min_1, max_2, index) in dp:
                curr[0] += dp[(min_1, max_2, index)]
                return

            tmp = curr[0]
            for array_1_num in range(nums[index], min_1-1, -1):
                array_2_num = nums[index] - array_1_num
                if array_2_num > max_2:
                    break
                dfs(array_1_num, array_2_num, index + 1)
            dp[(min_1, max_2, index)] = curr[0] - tmp

        dfs(0, float('inf'), 0)

        return curr[0] % (10 ** 9 + 7)
