class Solution {
    int n ;
    int mod = 1e9+7;
    int rec(int i,int p1,int p2,vector<int>&nums,vector<vector<vector<int>>>&dp){
        if(i==n) return 1;
        if(dp[i][p1][p2]!=-1) return dp[i][p1][p2];
        int temp = 0;
        for(int j = p1;j<=nums[i];j++){
            if(nums[i]-j<=p2){
                temp=(temp+rec(i+1,j,nums[i]-j,nums,dp))%mod;
            }
        }
        return dp[i][p1][p2]=temp;
    }
public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        vector<vector<vector<int>>>dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));
        return rec(0,0,50,nums,dp);
    }
};
