class Solution {
public:
    int mod =1e9+7;
    int f(int i, vector<int>&nums, int pu, int pd, vector<vector<vector<int>>>&dp)
    {
        if (i==nums.size())return 1;
        if (dp[i][pu][pd]!=-1)return dp[i][pu][pd];
        int ans=0;
        for (int k=pu;k<=nums[i];k++)
        {
            if (nums[i]-k>pd)continue;
            ans += f(i+1,nums,k,nums[i]-k, dp)%mod;
            ans %=mod;
        }
        return dp[i][pu][pd] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>>dp(nums.size(),vector<vector<int>>(52,vector<int>(52,-1)));
        return f(0,nums,0,51,dp)%mod;
    }
};
