class Solution {
public:
    int avic(std::vector<int>& nums)
    {
           const int MOD = 1e9 + 7;
        int n = nums.size();

        unordered_map<long long, int> memo;

        function<int(int, int, int)> f = [&](int index, int prev1, int prev2) -> int {
            if (index >= n) return 1;


            long long key = ((long long)index << 40) | ((long long)prev1 << 20) | prev2;
            if (memo.find(key) != memo.end()) return memo[key];

            int curr = nums[index];
            int ans = 0;

            for (int i = 0; i <= curr; ++i) {
                int curr1 = i;
                int curr2 = curr - i;

                if ((prev1 == -1 && prev2 == -1) || (curr1 >= prev1 && curr2 <= prev2)) {
                    ans = (ans + f(index + 1, curr1, curr2)) % MOD;
                }
            }

            memo[key] = ans;
            return ans;
        };

        return f(0, -1, -1);
    }
    int countOfPairs(std::vector<int>& nums) {
        return avic(nums);
    }
};
