class Solution {
public:

    const int N = 1e9 + 7;

    int f(vector<vector<int>>&dp, vector<int>&nums, int idx = 0, int prev_num = 0){
        if (idx == nums.size()) return 1;
        if (dp[idx][prev_num] != -1) return dp[idx][prev_num];
        int res=0;
        for (int i=prev_num; i<51;i++){
            if (i > nums[idx]) break;
            if (idx != 0){
                if (nums[idx] - i > nums[idx-1] - prev_num) continue;
            }
            res += f(dp,nums,idx+1,i);
            res %= N;
        }
        return dp[idx][prev_num] = res;

    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>dp(nums.size(),vector<int>(51,-1));
        int x = f(dp,nums);
        return x % N;
    }
};
