class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def solve(prev1, prev2, index):
            if index >= len(nums):
                return 1
            result = 0
            for i in range(nums[index] + 1):
                if i >= prev1 and nums[index] - i <= prev2:
                    result += solve(i, nums[index] - i, index + 1)
            return result

        return solve(-inf, inf, 0) % (int(10**9) + 7)
