#define MOD 1000000007
int DP[2001][51];

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int length = nums.size(), counts = 0;
        //memset(DP, 0, sizeof(DP));
        for (int i = 0; i <= nums[0]; ++i) {
            DP[0][i] = 1;
        }
        for (int i = 1; i < length; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                DP[i][j] = 0;
                for (int k = 0; k <= min(j, nums[i - 1]); ++k) {
                    if (nums[i - 1] - k < nums[i] - j) {
                        break;
                    }
                    DP[i][j] += DP[i - 1][k];
                    if (DP[i][j] >= MOD) {
                        DP[i][j] -= MOD;
                    }
                }
            }
        }
        for (int i = 0; i <= nums.back(); ++i) {
            counts += DP[length - 1][i];
            if (counts >= MOD) {
                counts -= MOD;
            }
        }
        return counts;
    }
};
