class Solution {
public:
    int countOfPairs(std::vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        std::vector<std::vector<int>> dp(51, std::vector<int>(51, 0));
        for (int x = 0; x <= nums[0]; ++x) {
            int y = nums[0] - x;
            if (y >= 0) {
                dp[x][y] = 1;
            }
        }
        for (int i = 1; i < n; ++i) {
            std::vector<std::vector<int>> new_dp(51, std::vector<int>(51, 0));

            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                if (y >= 0) {
                    int sum = 0;
                    for (int prev_x = 0; prev_x <= x; ++prev_x) {
                        for (int prev_y = y; prev_y <= nums[i-1] - prev_x; ++prev_y) {
                            sum = (sum + dp[prev_x][prev_y]) % MOD;
                        }
                    }
                    new_dp[x][y] = sum;
                }
            }

            dp = std::move(new_dp);
        }

        int result = 0;
        for (int x = 0; x <= 50; ++x) {
            for (int y = 0; y <= 50; ++y) {
                result = (result + dp[x][y]) % MOD;
            }
        }

        return result;
    }
};
