class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dpdfs(i: int, a: int, b: int) -> int:
            nonlocal nums
            if i == len(nums):
                return 1
            n = nums[i]
            ans = 0
            for x in range(a, n + 1):
                y = n - x
                if y <= b:
                    ans = (ans + dpdfs(i + 1, x, y)) % 1000000007
            return ans
        return dpdfs(0, 0, 1000)
