class Solution {
public:
    int MOD = 1e9+7;
    int dp[51][2005];
    int dfs(vector<int>& nums, int i, int j){
        if(j == nums.size())
            return 1;
        if(dp[i][j] == -1){
            dp[i][j] = 0;
            for(int x = i; x <= nums[j]; x++){
                int y = nums[j] - x;
                if(j > 0 && y > nums[j-1]-i)
                    continue;
                //cout << j << " " << x << y << endl;
                dp[i][j] = (dp[i][j] + dfs(nums, x, j + 1))  % MOD;
            }
        }
        return dp[i][j];
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return dfs(nums, 0, 0);
    }
};
