class Solution {
    vector<int> arr;
    int dp[2005][51][51];
    int mod=1e9+7;
    int n;
public:
    int rec(int ind,int aprev,int bprev){
        if(ind==n){
            return 1;
        }
        if(dp[ind][aprev][bprev]!=-1){
            return dp[ind][aprev][bprev];
        }
        int ans=0;
        for(int i=0;i<=arr[ind];i++){
            if(i<aprev){
                continue;
            }
            if(arr[ind]-i>bprev){
                continue;
            }
            ans=(ans+rec(ind+1,i,arr[ind]-i))%mod;
        }
        return dp[ind][aprev][bprev]=ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        n=(int)nums.size();
        arr=nums;
        int ans=0;
        for(int i=0;i<=nums[0];i++){
            ans=(ans+rec(1,i,nums[0]-i))%mod;
        }
        return ans;
    }
};
