class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9+7

        gaps = []


        # curr[j] means arr1[-1] = j and arr2[-1] = lastv-j


        curr = [0]*51

        for i,v in enumerate(nums):
            if i == 0:
                for j in range(v+1):
                    curr[j] = 1
                # print(curr)
                continue
            new_curr = [0]*51

            lastv = nums[i-1]

            for j in range(51):
                for jj in range(j+1):
                    if lastv - jj >= v-j and v-j >=0:
                        new_curr[j] += curr[jj] 
            # print(new_curr)

            curr = [v % mod for v in new_curr]
        return sum(curr)%mod
