class Solution:
    MOD = 10**9 + 7

    def countOfPairs(self, nums):
        n = len(nums)
        max_num = max(nums)

        dp = [[-1] * (max_num + 1) for _ in range(n)]

        return self.helper(0, 0, float('inf'), nums, dp)

    def helper(self, i, a, b, nums, dp):
        if i >= len(nums):
            return 1

        if dp[i][a] != -1:
            return dp[i][a]

        ans = 0

        for j in range(a, nums[i] + 1):
            sec = nums[i] - j

            if sec > b:
                continue

            ans = (ans + self.helper(i + 1, j, sec, nums, dp)) % self.MOD

        dp[i][a] = ans
        return dp[i][a]
