class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int MOD = 1000000007;
        int n = nums.size();
        vector<vector<int>> dp(51, vector<int>(51, 0));

        for (int i = 0; i <= nums[0]; ++i) {
            int k = nums[0] - i;
            dp[i][k] = 1;
        }

        for (int i = 1; i < n; ++i) {
            vector<vector<int>> temp(51, vector<int>(51, 0));
            vector<vector<int>> prefix(51, vector<int>(51, 0));

            for (int p = 0; p <= 50; ++p) {
                for (int q = 50; q >= 0; --q) {
                    prefix[p][q] = dp[p][q];
                    if (q < 50) {
                        prefix[p][q] = (prefix[p][q] + prefix[p][q + 1]) % MOD;
                    }
                }
            }

            for (int j = 0; j <= nums[i]; ++j) {
                int k = nums[i] - j;
                for (int p = 0; p <= j; ++p) {
                    temp[j][k] = (temp[j][k] + prefix[p][k]) % MOD;
                }
            }

            dp = temp;
        }

        int ans = 0;
        for (int j = 0; j <= 50; ++j) {
            for (int k = 0; k <= 50; ++k) {
                ans = (ans + dp[j][k]) % MOD;
            }
        }
        return ans;
    }
};
