class Solution {

    private val MAX_VALUE = 50
    private val MODULUS = 1_000_000_007

    fun countOfPairs(nums: IntArray): Int {
        if (nums.size == 1) return nums[0] + 1
        val pairCounts = Array(nums.size) { IntArray(MAX_VALUE + 1) }

        for (value in 0 .. MAX_VALUE) {
            pairCounts[0][value] = 1
        }

        for (i in 1 until nums.size) {
            for (previousValue in 0 .. nums[i - 1]) {
                val previousOtherValue = nums[i - 1] - previousValue
                for (nextValue in previousValue .. nums[i]) {
                    val previousCurrentValue = nums[i] - nextValue
                    if (previousCurrentValue > previousOtherValue) continue
                    // println("i: $i, previousValue: $previousValue, nextValue: $nextValue")

                    pairCounts[i][nextValue] += pairCounts[i - 1][previousValue]
                    pairCounts[i][nextValue] %= MODULUS
                }
            }
        }

        var pairCountSum = 0
        for (value in 0 .. MAX_VALUE) {
            pairCountSum = (pairCountSum + pairCounts.last()[value]) % MODULUS
        }

        return pairCountSum
    }
}
