MOD = 10 ** 9 + 7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        N = len(nums)

        @cache
        def dp(i, p1):
            if i == N:
                return 1
            p2 = nums[i-1] - p1
            res = 0 
            for x in range(p1+1):
                y = nums[i] - x
                if y >= p2:
                    res += dp(i+1, x)
            return res % MOD

        ans = 0
        for x in range(nums[0] + 1):
            ans += dp(1, x)

        return ans % MOD
