class Solution {

    private int MOD = 1000000007;
    private int[][][] memo;


    private int countPairs(int[] nums, int i, int minVal, int maxVal) {
        if (i == nums.length) {
            return 1;
        }

        if (memo[i][minVal][maxVal] != -1) {
            return memo[i][minVal][maxVal];
        }

        int count = 0;
        for (int x = minVal; x <= nums[i]; x++) {
            int y = nums[i] - x;
            if (y <= maxVal) {
                count = (count + countPairs(nums, i + 1, x, y)) % MOD;
            }
        }

        return memo[i][minVal][maxVal] = count;
    }

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        memo = new int[n][51][51];

        for (int[][] row : memo) {
            for (int[] col : row) {
                Arrays.fill(col, -1);
            }
        }

        return countPairs(nums, 0, 0, 50);
    }
}
