class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @cache
        def dfs(idx, pastMin, pastMax):
            if idx == n:
                return 1

            count = 0
            for i in range(nums[idx] + 1):
                curMin = i
                curMax = nums[idx] - i
                if curMin >= pastMin and curMax <= pastMax:
                    count += dfs(idx+1, curMin, curMax)
                    count %= MOD

            return count

        return dfs(0, 0, float('inf'))
