class Solution {
    public int mod = (int) 1e9+7;
    public int f(int i,int[] nums,int prev1,int prev2,int[][][] dp) {
        if(i==nums.length) return 1;
        int cnt = 0;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        for(int j=prev1;j<=nums[i];j++) {
            int j1=nums[i]-j;
            if(j1<=prev2) {
                cnt = (cnt%mod + f(i+1,nums,j,j1,dp)%mod)%mod;
            }
        }
        return dp[i][prev1][prev2]=cnt%mod;
    }
    public int countOfPairs(int[] nums) {
        int[][][] dp = new int[nums.length][51][51];
        for(int i=0;i<dp.length;i++) {
            for(int j=0;j<dp[0].length;j++) {
                Arrays.fill(dp[i][j],-1);
            }
        }
        return f(0,nums,0,nums[0],dp)%mod;
    }
}
