#define ll long long int
const ll MOD = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<ll>>> memo(size, vector<vector<ll>>(51, vector<ll>(51, -1)));

        return countPairs(0, 0, values[0], values, memo);
    }

private:

    ll countPairs(int index, int val1, int val2, vector<int>& values, vector<vector<vector<ll>>>& memo) {
        if (index == values.size()) return 1;

        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        ll result = 0;
        for (int i = val1; i <= values[index]; ++i) {
            for (int j = val2; j >= 0; --j) {
                if (isValidPair(i, j, values[index])) {
                    result = (result + countPairs(index + 1, i, j, values, memo)) % MOD;
                }
            }
        }

        return memoizeResult(index, val1, val2, result, memo);
    }


    bool isValidPair(int i, int j, int value) {
        return i + j == value;
    }

    ll memoizeResult(int index, int val1, int val2, ll result, vector<vector<vector<ll>>>& memo) {
        memo[index][val1][val2] = result;
        return result;
    }
};
