class Solution {
    const int MOD = 1e9 + 7;
public:
    int solve(vector<int>& nums, int ind, int pa, int pb, int& n, vector<vector<vector<int>>>& dp) {
        if (ind == n) return 1;
        if (dp[ind][pa][pb] != -1) return dp[ind][pa][pb];

        int ans = 0;
        for (int ca = pa; ca <= nums[ind]; ++ca) {
            int cb = nums[ind] - ca;
            if (cb <= pb) {
                ans = (ans + solve(nums, ind + 1, ca, cb, n, dp)) % MOD;
            }
        }
        return dp[ind][pa][pb] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, nums[0], n, dp);
    }
};
