class Solution {
public: int mod=1e9 + 7;
    int solve(int i,vector<int>& nums,int prev1,int prev2,vector<vector<vector<int>>>&dp){
        if(i>=nums.size())return 1;
        if(dp[i][prev1][prev2]!=-1)return dp[i][prev1][prev2];
        int ans=0;
        for(int ind=0;ind<=nums[i];ind++){
           for(int ind2=nums[i];ind2>=0;ind2--)
           if(prev1<=ind&&prev2>=ind2&&(ind+ind2)==nums[i]){
              ans=(ans%mod+solve(i+1,nums,ind,ind2,dp)%mod)%mod;
           }
        }
        return dp[i][prev1][prev2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>>dp(nums.size()+1,vector<vector<int>>(55,vector<int>(55,-1)));
        return solve(0,nums,0,nums[0],dp);
    }
};
