class Solution {
public:
    int mod=(int)1e9+7;
    int countOfPairs(vector<int>& v) {
        int n=v.size();
        vector<vector<int>>dp(n,vector<int>(51,0));
        for(int i=v[0];i>=0;i--){
            dp[0][i]=1;
        }
        for(int i=1;i<n;i++){
            int st=0;
            if(v[i]>v[i-1])
            st=v[i]-v[i-1];
            auto pre=dp[i-1];
            for(int i=1;i<51;i++){
                pre[i]+=pre[i-1];
                pre[i]%=mod;
            }
            for(int k=0;st+k<=v[i];k++){
                dp[i][st+k]=pre[k];
            }
        }
        long long ans=0;
        for(int i=0;i<=50;i++){
            ans+=dp[n-1][i];
            ans%=mod;
        }
        return ans;
    }
};
