class Solution {
    #define ll long long

public:
     ll mod = 1e9+7;

    int solveMem(int idx,int a,int b,vector<vector<vector<long long>>>&dp,vector<int>& nums){
     //base cases
     if(idx>=nums.size())
     return 1;

     if(dp[idx][a][b]!=-1)
     return dp[idx][a][b];
     ll ans(0);
     for(int i=a;i<=nums[idx];++i){
        for(int j=b;j>=0;--j){
          if(i+j == nums[idx])
          {
            ans+=(solveMem(idx+1,i,j,dp,nums));
            ans = ans%mod;
          }
        }
     }

     return dp[idx][a][b] = ans;
    }
    int countOfPairs(vector<int>& nums) {
         int n = nums.size();
         vector<vector<vector<long long>>>dp(n+1,vector<vector<long long>>(51,vector<long long>(51,-1)));
         return solveMem(0,0,nums[0],dp,nums);

    }
};
