class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<long long>> f(n, vector<long long>(51));

        for (int i=0; i<=a[0]; ++i)
            f[0][i] = 1;


        for (int i=1; i<n; ++i) {
            for (int a1=0; a1<=a[i]; ++a1) {
                int a2 = a[i] - a1;
                for (int last_a1=0; last_a1<=a1; ++last_a1) {
                    int last_a2 = a[i-1] - last_a1;
                    if (last_a2 >= a2)
                        f[i][a1] = (f[i][a1] + f[i-1][last_a1])% 1000000007;
                }
            }
        }


        long long ans = 0;
        for (int i=0; i<=50; ++i)
            ans = (ans + f[n-1][i]) % 1000000007;
        return ans;
    }
};
