class Solution {

    int n;
    int dp[2001][51][51];
    int f(int i,int prev1,int prev2,v(int )&arr){

        if(i==n){
            return 1;
        }
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2]%mod;

        int ct=0;
        for(int x=prev1;x<=arr[i];x++){
            int y=arr[i]-x;

            if(prev2>=y){
                ct+=f(i+1,x,y,arr);
                ct%=mod;
            }
        }

        return dp[i][prev1][prev2]= ct;

    }
public:
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));

        return f(0,0,nums[0],nums);
    }
};
