class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int sz = nums.size();
			using ll = long long;
			static constexpr int MOD = 1e9 + 7;
			int nb = nums.back();
			vector<vector<ll>> memo(sz + 1, vector<ll>(nb + 1, -1));
			auto dfs = [&](auto& self, int leftlen, int n1) {
				if (leftlen == 0) {
					return (ll)1;
				}
				ll& retv = memo[leftlen][n1];
				if (retv != -1) {
					return retv;
				}
				retv = 0;
				int sumv = nums[leftlen - 1], lasts = nums[leftlen];
				for (int j = n1; j >= 0; --j) {
					int n2 = sumv - j, lastn2 = lasts - n1;
					// if (n2 >= (lastn2)) {
					if (n2 >= (lastn2) && (n2 >= 0)) {
						retv += self(self, leftlen - 1, j);
							retv %= MOD;
					}
				}
				return retv;
			};
			ll rlt = 0;
			for (int i = nb; i >= 0; --i) {
				rlt += dfs(dfs, sz - 1, i);
				rlt %= MOD;
			}
			return rlt;
    }
};
