class Solution {
public:
   int MOD = 1e9 + 7;

    int helper(vector<int>& nums, int index, int lastArr1, int lastArr2, vector<vector<vector<int>>>& dp) {
        if (index == nums.size()) {
            return 1;  // Found a valid pair
        }

        if (dp[index][lastArr1][lastArr2] != -1) {
            return dp[index][lastArr1][lastArr2];
        }

        int count = 0;

        for (int x = lastArr1; x <= nums[index]; ++x) {
            int y = nums[index] - x;
            if (y <= lastArr2) {
                count = (count + helper(nums, index + 1, x, y, dp)) % MOD;
            }
        }

        return dp[index][lastArr1][lastArr2] = count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        // if(n==1) return 1;
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return helper(nums, 0, 0, 50, dp);
    }
};
