class Solution {
    private static final int MOD = 1_000_000_007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long result = 0;
        long[][] dp = new long[51][51];
        for (int i = 0; i <= nums[0]; i++) {
            dp[i][nums[0] - i] = 1;
        }

        // Iterate through the array
        for (int k = 1; k < n; k++) {
            long[][] newDp = new long[51][51];

            for (int i = 0; i <= 50; i++) {
                for (int j = 0; j <= 50; j++) {
                    if (dp[i][j] == 0) continue;

                    for (int x = i; x <= nums[k]; x++) {
                        int y = nums[k] - x;
                        if (y <= j) {
                            newDp[x][y] = (newDp[x][y] + dp[i][j]) % MOD;
                        }
                    }
                }
            }

            dp = newDp;
        }

        for (int i = 0; i <= 50; i++) {
            for (int j = 0; j <= 50; j++) {
                result = (result + dp[i][j]) % MOD;
            }
        }

        return (int) result;
    }
}
