class Solution {
public:
    int mod = 1e9+7;
    long long f(vector<int>&nums, int prev1,int prev2,int i,vector<vector<int>>& dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev1]!=-1){
            return dp[i][prev1];
        }
        long long ans = 0;
        if(i==0){
            for(int j = 0;j<=nums[i];j++){
                ans+=f(nums,j,nums[i]-j,i+1,dp);
                ans%=mod;
            }
        }else{
            for(int j=0;j<=nums[i];j++){
                if(j>=prev1 && nums[i]-j<=prev2){
                    ans+=f(nums,j,nums[i]-j,i+1,dp);
                    ans%=mod;
                }
            }
        }
        return dp[i][prev1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(51,-1));
        return f(nums,0,0,0,dp);
    }
};
