class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        memo = {}
        def bt(i, p1, p2):
            if i == len(nums): return 1
            if (i, p1, p2) in memo: return memo[(i, p1, p2)]
            count = sum(bt(i + 1, x, nums[i] - x) for x in range(p1, nums[i] + 1) if nums[i] - x <= p2)
            memo[(i, p1, p2)] = count % MOD
            return count % MOD
        return bt(0, 0, nums[0])
