class Solution {
public:
    int mod=1e9+7;
    long solve(int i,int prev1,int prev2,vector<int>& nums,vector<vector<vector<int>>>& dp){
        int n=nums.size();
        if(i>=n){
            return 1;
        }
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];

        long cnt=0;
        for(int j=0;j<=nums[i];j++){
            int x = j;
            int y = nums[i]-j;

            if(x>=prev1 && y<=prev2){
                cnt = (cnt%mod + solve(i+1,x,y,nums,dp)%mod)%mod;
            }
        }

        return dp[i][prev1][prev2] = cnt%mod;



    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();

        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));


        long ans = solve(0,0,50,nums,dp);

        // ans = ans*2;
        ans%=mod;

        return ans;

    }
};
