#include <iostream>
#include <vector>
using namespace std;

#define ll long long int

class Solution {
public:
    ll solve(int ind, int ind1, int ind2, const vector<int>& nums, vector<vector<vector<ll>>>& dp) {
        if (ind >= nums.size()) return 1;
        ll mod = 1e9 + 7;

        // Check if the result is already computed
        if (dp[ind][ind1][ind2] != -1) return dp[ind][ind1][ind2];

        ll ans = 0;
        // Iterate over possible values for arr1[ind] and arr2[ind]
        for (int i = ind1; i <= nums[ind]; ++i) {
            for (int j = ind2; j >= 0; --j) {
                if (i + j == nums[ind]) {
                    ans = (ans + solve(ind + 1, i, j, nums, dp)) % mod;
                }
            }
        }

        // Memoize the result
        return dp[ind][ind1][ind2] = ans;
    }

    int countOfPairs(const vector<int>& nums) {
        int n = nums.size();
        // Initialize the dp table with -1 (uncomputed state)
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        // Call the solve function starting from index 0
        return solve(0, 0, nums[0], nums, dp);
    }
};
