class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        //solution 1
        //
        int preIdx = 0, curIdx = 1, md = 1e9 + 7, n = nums.size();
        int dp[2][51]{};

        //base case init
        for(int x = 0; x <= nums[0]; ++x) dp[0][x] = 1;

        for(int i = 1; i < n; ++i){
            memset(dp[curIdx], 0, sizeof(dp[curIdx]));

            for(int x = 0; x <= nums[i]; ++x){
                int y = nums[i] - x;

                for(int prex = x; prex >= 0; --prex){
                    int prey = nums[i-1] - prex;

                    //if(prey >= 0 && prey >= y){}
                    if(prey >= y) dp[curIdx][x] = (dp[curIdx][x] + dp[preIdx][prex]) % md;
                }
            }

            swap(preIdx, curIdx);
        }

        int ret = 0;
        for(const auto &x: dp[preIdx]) ret = (ret + x) % md;

        return ret;
        //end of solution 1
    }
};
