class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        MOD = 10**9 + 7
        mx = max(nums)
        @cache
        def dp(idx, previ, prevd):
            nonlocal mx
            if idx>=len(nums):
                return 1
            res = 0
            for i in range(mx+1):
                if i>=previ and (nums[idx]-i)<=prevd and (nums[idx]-i)>=0:
                    res += dp(idx+1, i, nums[idx]-i)%MOD
            return res
        return dp(0, -1, 100)%MOD
