class Solution {
public:
    static const int mod=1e9+7;
    int dp[2010][55];
    int add(int a,int b){
        return (a%mod+b%mod)%mod;
    }
    int f(int i,int pre,vector<int>&nums){
        if(i==nums.size())return 1;
        int &ans=dp[i][pre];
        if(ans!=-1)return ans;
        ans=0;
        for(int curr=0;curr<=nums[i];++curr){
            if((curr>=pre) and ((nums[i-1]-pre)>=nums[i]-curr)){
                // cout<<"called\n";
                ans=(ans%mod+f(i+1,curr,nums)%mod)%mod;
            }
        }
        return ans%mod;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof dp);
        int ans=0;
        for(int i=0;i<=nums[0];++i){
            ans=(ans%mod+f(1,i,nums)%mod)%mod;
        }
        return ans;
    }
};
