class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = defaultdict(Counter)
        for i in range(nums[0] + 1):
            dp[0][i] += 1
        for i in range(1, n):
            for j1 in range(nums[i] + 1):
                j2 = nums[i] - j1
                for k1 in range(nums[i - 1] + 1):
                    k2 = nums[i - 1] - k1
                    if k1 <= j1 and k2 >= j2:
                        dp[i][j1] += dp[i - 1][k1]
        return sum(dp[n - 1].values()) % 1000000007
