class Solution {
    int MOD=1000000007;
public:
    int countOfPairs(vector<int>& nums)
    {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n+1, vector<vector<int>>(51, vector<int>(51, 0)));

        for(int i=0;i<=nums[0];i++)
        {
            int k=nums[0]-i;
            dp[1][i][k]=1;
        }

        for(int i=1;i<n;i++)
        {
            for(int j=0;j<=nums[i-1];j++)
            {
                for(int k=0; k<=nums[i-1];k++)
                {
                    if(dp[i][j][k]>0)
                    {
                        for(int dj=j;dj<=nums[i];dj++)
                        {
                            int dk=nums[i]-dj;
                            if(dk<=k) dp[i+1][dj][dk] = (dp[i+1][dj][dk]+dp[i][j][k])%MOD;
                        }
                    }
                }
            }
        }

        int ans=0;
        for(int j=0;j<51;j++)
        {
            for(int k=0;k<51;k++) ans=(ans+dp[n][j][k])%MOD;
        }

        return ans;
    }
};
