class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int mod = 1e9 + 7;
        int n = nums.size();
        nums.push_back(50);
        vector<vector<int>> dp(n, vector<int>(52, -1));
        auto dfs = [&] (auto&& dfs, int i, int pre) {
            if(i < 0) {
                return 1;
            }
            if(dp[i][pre] != -1) {
                return dp[i][pre];
            }
            int res = 0;
            for(int j=0; j<=nums[i]; j++) {
                if(j <= pre && nums[i] - j >= nums[i+1] - pre) {
                    res = (res + dfs(dfs, i-1, j)) % mod;
                }
            }
            dp[i][pre] = res;
            return res;
        };
        return dfs(dfs, n-1, 51);
    }
};
