class Solution {
    int MOD = 1e9 + 7;
    int solve(vector<int>& nums,int i,int prev1,int prev2,vector<vector<vector<int>>>& dp){
        int n = nums.size();
        if(i == n) return 1;
        if(dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

        int ways = 0;
        for(int val1 = prev1 ; val1 <= 50 ; val1++){
            for(int val2 = prev2 ; val2 >= 0 ; val2--){
                if(val1 + val2 == nums[i]){
                    ways = (ways + solve(nums,i+1,val1,val2,dp)) % MOD;
                }
            }
        }

        return dp[i][prev1][prev2] = ways;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>> (51,vector<int> (51,-1)));
        return solve(nums,0,0,50,dp);
    }
};
