class Solution {
public:
    int N=1e9+7;
    int solve(int ind,int prev,vector<int>& nums,vector<vector<int>>&dp){
        if(ind==nums.size()){
            return 1;
        }
        if(dp[ind][prev+1]!=-1)return dp[ind][prev+1];
        int ans=0;
        if(prev!=-1){
            for(int curr=prev;curr<=nums[ind];curr++){
                int currsecond=nums[ind]-curr;
                int prevsecond=nums[ind-1]-prev;
                if(currsecond<=prevsecond and curr>=prev){
                    ans=(ans%N+solve(ind+1,curr,nums,dp)%N)%N;
                }
            }
        }
        else{
            for(int curr=0;curr<=nums[ind];curr++){
                ans=(ans%N+solve(ind+1,curr,nums,dp)%N);
            }
        }
        return dp[ind][prev+1]=ans;

    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>dp(nums.size(),vector<int>(53,-1));
        return solve(0,-1,nums,dp);
    }
};
