class Solution {
public:
    int mod= 1e9+7;
    int n;
    int dp[2002][52];
    int func(int ind, vector<int> &nums, int prev)
    {
        if(ind==n)
            return 1;

        if(dp[ind][prev]!=-1)
            return dp[ind][prev];
        int total=0;
        for(int x=0; x<=nums[ind]; x++)
        {
            if(ind==0)
            {
                total= (total+ func(ind+1, nums, x))%mod;
            }
            else if(ind>0 && x>=prev && nums[ind-1]-prev>=nums[ind]-x)
            {
                total= (total+func(ind+1, nums, x))%mod;
            }
        }
        return dp[ind][prev]= total;
    }
    int countOfPairs(vector<int>& nums) 
    {
        n=nums.size();
        memset(dp, -1, sizeof(dp));
        return func(0, nums, 0);
    }
};
