class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dfs(i, prev_incr, prev_decr):
            if i >= len(nums):
                return 1
            i_incr = nums[i]
            i_decr = 0
            total = 0
            while i_incr <= nums[i] and i_incr >= prev_incr and i_decr <= prev_decr:
                total += dfs(i+1, i_incr, i_decr)
                i_incr -= 1
                i_decr += 1
            return total % (10**9 + 7)

        return dfs(0,0,float('inf'))
