class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7

        @lru_cache(None)
        def dfs(idx, prev):
            if idx == n:
                return 1

            total = 0
            for cur in range(prev, nums[idx] + 1):
                if nums[idx] - cur >= 0 and (idx == 0 or nums[idx] - cur <= nums[idx - 1] - prev):
                    total = (total + dfs(idx + 1, cur)) % MOD

            return total % MOD

        return dfs(0, 0)
