class Solution {
public:
    int n, mod = 1e9+7;
    vector<int> arr;
    int dp[2001][53][53];
    int f(int i,int prev1,int prev2){
        if(i==n) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int ans = 0;
        for(int val=0;val<=arr[i];val++){
            if(i!=0 && val>=prev1 && arr[i]-val<=prev2){
                ans += f(i+1,val,arr[i]-val);
                ans %= mod;
            }
            else if(i==0){
                ans += f(i+1,val,arr[i]-val);
                ans %= mod;
            }
        }
        return dp[i][prev1][prev2] = (ans%mod);
    }
    int countOfPairs(vector<int>& nums) {
        arr = nums;
        n = arr.size();
        memset(dp,-1,sizeof(dp));
        return f(0,51,51);
    }
};
