class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7
        dp = [[0] * 51 for _ in range(n)]
        for j in range(min(50, nums[0]) + 1):
            dp[0][j] = 1
        for i in range(1, n):
            for j in range(min(50, nums[i]) + 1):
                for jj in range(max(j, nums[i-1] - nums[i] + j), min(50, nums[i-1]) + 1):
                    dp[i][j] += dp[i-1][jj] % MOD
        # for r in dp:
        #     print(r)
        return sum(dp[n-1][j] for j in range(51)) % MOD
