#define ll long long

class Solution {
public:
    ll dp[2002][52];
    const ll mod = 1e9+7;
    ll helper(int i,vector<int> &nums,int ls){
        if(i==nums.size()){
            return 1;
        }
        ll ans = 0;
        if(dp[i][ls]!=-1) return dp[i][ls];
        if(i==0){
            for(int j=0;j<=nums[i];j++){
                ans = (ans + helper(i+1,nums,j)%mod)%mod;
                ans %= mod;
            }
        }
        else{
            for(int j=0;j<=nums[i];j++){
                if(j>=ls && (nums[i-1]-ls)>=(nums[i]-j)){
                    ans = (ans + helper(i+1,nums,j)%mod)%mod;
                    ans %= mod;
                }
            }
        }
        return dp[i][ls] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return helper(0,nums,0);
    }
};
